import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * Created by sw on 2018-06-27.
 */
public class FindAllAnagramsInAString {
    static public List<Integer> findAnagrams(String s, String p) {
        //模版比较法 时间复杂度较高
        char[] pArr = p.toCharArray();
        HashMap<Character, Integer> pMap = new HashMap<>();
        //首先分析模版串的字母分布
        for (int i = 0; i < pArr.length; i++) {
            if (pMap.containsKey(pArr[i])) {
                int num = pMap.get(pArr[i]) + 1;
                pMap.put(pArr[i], num);
            } else {
                pMap.put(pArr[i], 1);
            }
        }

        List<Integer> ans = new ArrayList<>();
        for (int i = 0; i < s.length(); i++) {
            for (int j = i; j < s.length(); j++) {
                String subStr = s.substring(i, j + 1);
                HashMap<Character, Integer> subMap = new HashMap<>();
                //获取子串的字母分布
                for (int k = 0; k < subStr.length(); k++) {
                    if (subMap.containsKey(subStr.charAt(k))) {
                        int num = subMap.get(subStr.charAt(k)) + 1;
                        subMap.put(subStr.charAt(k), num);
                    } else {
                        subMap.put(subStr.charAt(k), 1);
                    }
                }
                //对比子串与模版之间的差异
                boolean flag = false;
                if (subMap.size() != pMap.size()) {
                    continue;
                } else {
                    for (Character ch : subMap.keySet()) {
                        if (pMap.containsKey(ch) && subMap.containsKey(ch)) {
                            int n1 = pMap.get(ch);
                            int n2 = subMap.get(ch);
                            if (n1 != n2) {
                                flag = false;
                                break;
                            } else {
                                flag = true;
                            }
                        } else {
                            flag = false;
                            break;
                        }
                    }
                }
                //是否全部一致
                if (flag) {
                    ans.add(i);
                }
            }
        }
        return ans;
    }

    static public List<Integer> findAnagrams2(String s, String p) {
        List<Integer> ans = new ArrayList<>();
        char[] pArr = p.toCharArray();
        HashMap<Character, Integer> pMap = new HashMap<>();
        HashMap<Character, Integer> tempMap = new HashMap<>();
        //首先分析模版串的字母分布
        for (char c : p.toCharArray())
            pMap.put(c, pMap.getOrDefault(c, 0) + 1);

        //按滑动窗口分析
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            char leftC; //记录最左边的字符

            tempMap.put(c, tempMap.getOrDefault(c, 0) + 1);

            if (i >= p.length()) {
                leftC = s.charAt(i - p.length());
                if (tempMap.get(leftC) == 1) {
                    tempMap.remove(leftC);
                } else {
                    tempMap.put(leftC, tempMap.get(leftC) - 1);
                }
            }

            if (tempMap.equals(pMap)) {
                ans.add(i + 1 - p.length());
            }
        }
        return ans;

    }

    public static void main(String[] args) {
        List<Integer> ans = findAnagrams2("", "a");
        for (int i = 0; i < ans.size(); i++) {
            System.out.println(ans.get(i));
        }
    }
}
